import React from "react";
import Board from "./Board";
import Cell from "./Cell";


const App = () => {

    // Check if a row is valid
    const isRowValid = (board, row, value) => {
        for (let i = 0; i < 9; i++) {
            if (board[row][i] === value) {
            return false;
            alert
            }
        }
        return true;
    };

    // Check if a column is valid
    const isColumnValid = (board, column, value) => {
        for (let i = 0; i < 9; i++) {
        if (board[i][column] === value) {
            return false;
        }
        }
        return true;
    };

    // Check if a sub-box is valid
    const isSubBoxValid = (board, row, column, value) => {
        const subBoxRow = Math.floor(row / 3) * 3;
        const subBoxColumn = Math.floor(column / 3) * 3;

        for (let i = subBoxRow; i < subBoxRow + 3; i++) {
            for (let j = subBoxColumn; j < subBoxColumn + 3; j++) {
                if (board[i][j] === value) {
                    return false;
                }
            }
        }
        return true;
    };

    // Check if the user input adheres to the rules of Sudoku
    const isInputValid = (board, row, column, value) => {
        return isRowValid(board, row, value) && 
               isColumnValid(board, column, value) && 
               isSubBoxValid(board, row, column, value);
    };


    const isRed = () => {
        alert("this item is not red")
    }
    return (

        <main>
            <div className="App">
                <h1>Sudoku</h1>
             </div>
            <Board isRed={isRed}
                   isRowValid={isRowValid}
                   isColumnValid={isColumnValid}
                   isSubBoxValid={isSubBoxValid}>
            </Board>
        </main>

    )
}

export default App;
