import React, { useState } from 'react';
import Cell from './Cell';

const Board = ({ solveSudoku}) => {

  const [boardState, setBoardState] = useState([
    [0, 0, 0, 2, 6, 0, 7, 0, 1],
    [6, 8, 0, 0, 7, 0, 0, 9, 0],
    [1, 9, 0, 0, 0, 4, 5, 0, 0],
    [8, 2, 0, 1, 0, 0, 0, 4, 0],
    [0, 0, 4, 6, 0, 2, 9, 0, 0],
    [0, 5, 0, 0, 0, 3, 0, 2, 8],
    [0, 0, 9, 3, 0, 0, 0, 7, 4],
    [0, 4, 0, 0, 5, 0, 0, 3, 6],
    [7, 0, 3, 0, 1, 8, 0, 0, 0]
  ]);
  const isRowValid = (board, row, value) => {
    console.log(value)
    for (let i = 0; i < 9; i++) {
        if (board[row - 1][i] === value) {
            alert("contains duplicate number")

        }
    }
    return board;
};

// Check if a column is valid
const isColumnValid = (board, column, value) => {
    for (let i = 0; i < 9; i++) {
        if (board[i][column - 1] === value) {
            alert("contains duplicate number")
            return false;
        }
    }
    return board;
};

// Check if a sub-box is valid
const isSubBoxValid = (board, row, column, value) => {
    let startRow = row - (row % 3);
    let startCol = column - (column % 3);

    for (let i = 0; i <  3; i++) {
        for (let j = 0; j < 3; j++) {
            if (board[i + startRow][j + startCol] === value) {
                alert("contains duplicate number")
                return false;
            }
        }
    }
    return board;
};

// Check if the user input adheres to the rules of Sudoku
const isInputValid = (board, row, column, value) => {
    return isRowValid(board, row, value) && 
           isColumnValid(board, column, value) && 
           isSubBoxValid(board, row, column, value);
};

  const handleChange = (event, rowIndex, columnIndex) => {
      const newValue = parseInt(event.target.value);
      const newBoardState = [...boardState];
      newBoardState[rowIndex][columnIndex] = newValue;
      setBoardState(newBoardState);
      isInputValid(newBoardState,rowIndex, columnIndex, newValue)

      let solution = solveSudoku(boardState, 0, 0)
      console.log(solution)
      // if (newBoardState === solution) {
      //   alert("Puzzle Solved!")
      // }
  }

  // const solve = () => {
  //   console.log(boardState)
  //   solveSudoku(boardState, 0, 0)
  // }

  const reset = () => {
    const restart = [
      [0, 0, 0, 2, 6, 0, 7, 0, 1],
      [6, 8, 0, 0, 7, 0, 0, 9, 0],
      [1, 9, 0, 0, 0, 4, 5, 0, 0],
      [8, 2, 0, 1, 0, 0, 0, 4, 0],
      [0, 0, 4, 6, 0, 2, 9, 0, 0],
      [0, 5, 0, 0, 0, 3, 0, 2, 8],
      [0, 0, 9, 3, 0, 0, 0, 7, 4],
      [0, 4, 0, 0, 5, 0, 0, 3, 6],
      [7, 0, 3, 0, 1, 8, 0, 0, 0]
    ]
    setBoardState(restart)
  }
  // Render the board
  return (
    <div className="board">
      {boardState.map((row, rowIndex) => (
        <div className="board-row" key={rowIndex}>
          {row.map((cell, columnIndex) => (
            <Cell
              key={`${rowIndex}-${columnIndex}`}
              value={cell}
              readOnly={cell !== 0}
              onChange={(e) => handleChange(e, rowIndex, columnIndex)}
            />
          ))}
        </div>

      ))}
      {/* <button onClick={solve}>
                Solve
      </button> */}

      <button onClick={isInputValid}>
                Check
      </button>

      <button onClick={reset}>
                Reset
      </button>
    </div>
  );
};

export default Board;
